import React, { useState } from 'react';
import './App.css';

const Task = ({ task, onEdit, onDelete, onToggleCompleted }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [editedTask, setEditedTask] = useState(task);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEditedTask((prevTask) => ({ ...prevTask, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onEdit(task.id, editedTask);
    setIsEditing(false);
  };

  const handleCancel = () => {
    setIsEditing(false);
  };

  return (
    <li>
      {isEditing ? (
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            name="name"
            value={editedTask.name}
            onChange={handleChange}
            placeholder="Task name"
          />
          <input
            type="text"
            name="description"
            value={editedTask.description}
            onChange={handleChange}
            placeholder="Task description"
          />
          <button type="submit">Save</button>
          <button type="button" onClick={handleCancel}>Cancel</button>
        </form>
      ) : (
        <>
          <h2 onClick={() => setIsEditing(true)}>{task.name}</h2>
          <p onClick={() => setIsEditing(true)}>{task.description}</p>
        </>
      )}
      <button onClick={() => toggleCompleted(task.id)}>
        {task.completed ? 'Mark Incomplete' : 'Mark Complete'}
      </button>
      <button onClick={() => deleteTask(task.id)}>Delete</button>
    </li>
  );
};

const App = () => {
  const [tasks, setTasks] = useState([]);
  const [input, setInput] = useState({ name: '', description: '' });

  const handleChange = e => {
    const { name, value } = e.target;
    setInput(prevInput => ({ ...prevInput, [name]: value }));
  };

  const addTask = () => {
    setTasks([...tasks, { ...input, completed: false, id: Date.now() }]);
    setInput({ name: '', description: '' });
  };

  const deleteTask = (id) => {
    const updatedTasks = tasks.filter((task) => task.id !== id);
    setTasks(updatedTasks);
  };

  const toggleCompleted = (id) => {
    const updatedTasks = tasks.map((task) =>
      task.id === id ? { ...task, completed: !task.completed } : task
    );
    setTasks(updatedTasks);
  };

  const editTask = (id, updatedTask) => {
    const updatedTasks = tasks.map((task) =>
      task.id === id ? updatedTask : task
    );
    setTasks(updatedTasks);
  };

  return (
    <div className="App">
      <h1>Task Manager</h1>
      <form onSubmit={e => { e.preventDefault(); addTask(); }}>
        <input
          type="text"
          name="name"
          value={input.name}
          onChange={handleChange}
          placeholder="Task name"
        />
        <input
          type="text"
          name="description"
          value={input.description}
          onChange={handleChange}
          placeholder="Task description"
        />
        <button type="submit">Add Task</button>
      </form>
      <ul>
        {tasks.map((task) => (
          <Task
            key={task.id}
            task={task}
            onEdit={editTask}
            onDelete={deleteTask}
            onToggleCompleted={toggleCompleted}
          />
        ))}
      </ul>
      </div>
  );
};

export default App;
